modal.showDialogForm('Create', 'mkdir_form', null, function(){
	var oForm = $(modal.getForm());	
	var elementToCheck=(oForm['dirname']);
	if(ajaxplorer.getContextHolder().fileNameExists($(elementToCheck).getValue()))
